package vn.com.kodergang.shop.dao.impl;import org.springframework.stereotype.Repository;import vn.com.kodergang.common.generics.impl.GenericDaoImpl;import vn.com.kodergang.shop.dao.AdPermissionDao;import vn.com.kodergang.shop.entity.AdPermission;import java.util.Collections;import java.util.HashMap;import java.util.List;import java.util.Map;@Repository("AdPermissionDaoImpl")public class AdPermissionDaoImpl extends GenericDaoImpl<AdPermission, Integer> implements AdPermissionDao {    public Map getPermissionByUser(int userId) {        Map ret = new HashMap();        String sql = "select code from ad_permission where user_Id=:userId";        List<Map<String, Object>> lst = namedParameterJdbcTemplate.queryForList(sql, Collections.singletonMap("userId", userId));        ret.put("permission", lst);        return ret;    }    @Override    public List<AdPermission> getByCondition(Map map) {        String sql = "select p.PERMISSION_ID permissionId" +                "   from AD_PERMISSION p" +                "        join AD_USER us on (us.USER_ID = p.USER_ID and us.STATUS='ACTIVE') " +                "   where us.USER_ID = :userId " +                "   and p.CODE = :permission";        return getByCondition(map, sql, AdPermission.class);    }}