package vn.com.kodergang.shop.service.impl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Qualifier;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import vn.com.kodergang.common.generics.GenericDao;import vn.com.kodergang.common.generics.impl.GenericServiceImpl;import vn.com.kodergang.shop.dao.AdUserDao;import vn.com.kodergang.shop.entity.AdUser;import vn.com.kodergang.shop.service.AdUserService;import java.util.List;import java.util.Map;@Service("AdUserServiceImpl")@Transactionalpublic class AdUserServiceImpl extends GenericServiceImpl<AdUser, Integer> implements AdUserService {    @Autowired    AdUserDao dao;    public AdUserServiceImpl() {    }    @Autowired    public AdUserServiceImpl(@Qualifier("AdUserDaoImpl") GenericDao<AdUser, Integer> genericDao) {        super(genericDao);        this.dao = (AdUserDao) genericDao;    }    @Override    public boolean existsEmail(String email, String userName) {        return dao.existsEmail(email, userName);    }    @Override    public boolean existsUserName(String userName) {        return dao.existsUserName(userName);    }    @Override    public AdUser getUserByUserName(String userName) {        return dao.getUserByUserName(userName);    }}